import React, { useState } from 'react';
import axios from 'axios';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await axios.post('http://localhost:3000/api/login', {
                username,
                password,
            });

            localStorage.setItem('token', response.data.token);
        } catch (error) {
            console.error('Failed to log in:', error);
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
            <div className="relative py-3 sm:max-w-xl sm:mx-auto">
                <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-light-blue-500 \
                shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl"></div>
                <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label htmlFor="username" className="block text-sm">
                                Username
                            </label>
                            <input id="username" type="text" value={username} onChange={(e) => setUsername(e.target.value)} 
                            className="w-full px-5 py-1 text-gray-700 bg-gray-200 rounded" required />
                        </div>
                        <div className="mt-5">
                            <label htmlFor="password" className="block text-sm">
                                Password
                            </label>
                            <input id="password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} 
                            className="w-full px-5 py-1``` />
                            <input  value={password} onChange={(e) => setPassword(e.target.value)} 
                            className="w-full px-5 py-1 text-gray-700 bg-gray-200  />
                        </div>
                        <div className="mt-6">
                            <button type="submit" className="px-4 py-2 text-white font-semibold bg-blue-500 rounded hover:bg-blue-600">
                                Log In</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
