import React, { useState, useEffect } from 'react';
import UserCard from './Card';
import axios from 'axios';

function Dashboard() {
 const [users, setUsers] = useState([]);
 const [loading, setLoading] = useState(false);
 const [error, setError] = useState(null);
 const [showPopup, setShowPopup] = useState(false); // New state for showing the popup
 const [inputValues, setInputValues] = useState({ name: '', email: '', phone: '' }); // New state for holding the input values
 const [currentUserId, setCurrentUserId] = useState(null); // New state for storing the current user's ID

 useEffect(() => {
 setLoading(true);
 axios.get('http://localhost:5000/api/users')
  .then(response => {
    setUsers(response.data);
    setLoading(false);
  })
  .catch(error => {
    console.error(error);
    setError(error.message);
    setLoading(false);
  });
 }, []);

 const handleInputChange = (event) => {
 setInputValues({ ...inputValues, [event.target.name]: event.target.value });
 };

 const handleSaveClick = () => {
 axios.post('http://localhost:5000/api/addUser', { ...inputValues, userId: currentUserId }) // Include the user's ID in the request body
  .then(response => {
    setUsers([...users, response.data.user]);
    setShowPopup(false);
  })
  .catch(error => {
   console.error(error.response.data); // Log error.response.data instead of error
   setError(error.message);
  });
 };

 const handleCancelClick = () => {
 setShowPopup(false);
 };

 if (loading) {
 return <p>Loading...</p>;
 }

 if (error) {
 return <p>Error: {error}</p>;
 }

 return (
  <div>
    <h1>Users</h1>
    <button onClick={() => setShowPopup(true)}>Add User</button>
    {showPopup && (
      <div>
        <input type="text" name="name" value={inputValues.name} onChange={handleInputChange} placeholder="Name" />
        <input type="email" name="email" value={inputValues.email} onChange={handleInputChange} placeholder="Email" />
        <input type="tel" name="phone" value={inputValues.phone} onChange={handleInputChange} placeholder="Phone Number" />
        <button onClick={handleSaveClick}>Save</button>
        <button onClick={handleCancelClick}>Cancel</button>
      </div>
    )}
    <div className="flex flex-wrap justify-around">
      {users.length > 0 ? (
        users.map(user => (
          <UserCard key={user._id} user={user} />
        ))
      ) : (
        <p>No users added</p>
      )}
    </div>
  </div>
 ); 
}

export default Dashboard;
