import React, { useState, useEffect } from 'react';
import UserCard from './Card';
import axios from 'axios';

function Dashboard() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showPopup, setShowPopup] = useState(false); // New state for showing the popup
  const [inputValues, setInputValues] = useState({ name: '', email: '', phone: '' }); // New state for holding the input values

  useEffect(() => {
    setLoading(true);
    axios.get('http://localhost:5000/api/addedUsers')
      .then(response => {
        setUsers(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error(error);
        setError(error.message);
        setLoading(false);
      });
  }, []);

  const handleInputChange = (event) => {
    setInputValues({ ...inputValues, [event.target.name]: event.target.value });
  };

  // Add this function inside your Dashboard component
const handleDeleteUser = (userId) => {
  axios.delete(`http://localhost:5000/api/addedUsers/${userId}`)
    .then(response => {
      // Filter out the deleted user
      const updatedUsers = users.filter(user => user._id !== userId);
      setUsers(updatedUsers);
    })
    .catch(error => {
      console.error(error);
      setError(error.message);
    });
 };
 
 const handleUpdateUser = (userId) => {
  // Set the current user details in the input fields
  const userToUpdate = users.find(user => user._id === userId);
  setInputValues({ name: userToUpdate.name, email: userToUpdate.email, phone: userToUpdate.phone });
  setShowPopup(true);
 };
 

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.reload();
  };

  const handleSaveClick = () => {
    axios.post('http://localhost:5000/api/addedUsers', inputValues)
      .then(response => {
        setUsers([...users, response.data.user]);
        setShowPopup(false);
      })
      .catch(error => {
        console.error(error.response.data); // Log error.response.data instead of error
        setError(error.message);
      });
  };

  const handleCancelClick = () => {
    setShowPopup(false);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  return (
    <div>
      {/* <h1>Users</h1> */}
      <div className="flex justify-between items-center p-6">
        <h1>Added Users</h1>
        <button onClick={handleLogout} className="px-4 py-2 rounded text-white bg-gray-500 hover:bg-red-700">Logout</button>
      </div>
      <div className="flex justify-center">
        <button className="px-4 py-2 rounded bg-blue-500 hover:bg-blue-700 text-white" onClick={() => setShowPopup(true)}>Add User</button>
      </div>
      {showPopup && (
        <div>
          <input type="text" name="name" value={inputValues.name} onChange={handleInputChange} placeholder="Name" />
          <input type="email" name="email" value={inputValues.email} onChange={handleInputChange} placeholder="Email" />
          <input type="tel" name="phone" value={inputValues.phone} onChange={handleInputChange} placeholder="Phone Number" />
          <button onClick={handleSaveClick}>Save</button>
          <button onClick={handleCancelClick}>Cancel</button>
        </div>
      )}
      <div className="flex flex-wrap justify-around">
        {users.length > 0 ? (
          users.map(user => (
            <UserCard key={user._id} user={user} deleteUser={() => handleDeleteUser(user._id)} />
          ))
        ) : (
          <p>No users added</p>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
