import React, { useState, useEffect } from 'react';
import UserCard from './Card';
import axios from 'axios';

function Dashboard() {
 const [users, setUsers] = useState([]);
 const [addedUsers, setAddedUsers] = useState([]); // New state for addedUsers
 const [loading, setLoading] = useState(false);
 const [error, setError] = useState(null);
 const [showPopup, setShowPopup] = useState(false); // New state for showing the popup
 const [inputValues, setInputValues] = useState({ name: '', email: '', phone: '' }); // New state for holding the input values

 useEffect(() => {
  setLoading(true);
  axios.get('http://localhost:5000/api/users')
    .then(response => {
      setUsers(response.data);
      setLoading(false);
    })
    .catch(error => {
      console.error(error);
      setError(error.message);
      setLoading(false);
    });

  // Fetch addedUsers
  axios.get('http://localhost:5000/api/addedUsers') // Replace with your actual API endpoint
    .then(response => {
      setAddedUsers(response.data);
      setLoading(false);
    })
    .catch(error => {
      console.error(error);
      setError(error.message);
      setLoading(false);
    });
 }, []);

 // Rest of your code...

 return (
  <div>
    <h1>Users</h1>
    <button onClick={() => setShowPopup(true)}>Add User</button>
    {showPopup && (
      <div>
        <input type="text" name="name" value={inputValues.name} onChange={handleInputChange} placeholder="Name" />
        <input type="email" name="email" value={inputValues.email} onChange={handleInputChange} placeholder="Email" />
        <input type="tel" name="phone" value={inputValues.phone} onChange={handleInputChange} placeholder="Phone Number" />
        <button onClick={handleSaveClick}>Save</button>
        <button onClick={handleCancelClick}>Cancel</button>
      </div>
    )}
    <div className="flex flex-wrap justify-around">
      {users.length > 0 ? (
        users.map(user => (
          <UserCard key={user._id} user={user} />
        ))
      ) : (
        <p>No users added</p>
      )}
      {/* Map over addedUsers */}
      {addedUsers.length > 0 ? (
        addedUsers.map(user => (
          <UserCard key={user._id} user={user} />
        ))
      ) : (
        <p>No added users</p>
      )}
    </div>
  </div>
 ); 
}

export default Dashboard;
